import logo from './logo.svg';
import './App.css';
import data from './data.json';

class App extends Component{

  constructor(){
    super();
    this.state = {
      monsters:[],
      searchField: ''

    }  
  }

  componentDidMount(){
    fetch('https://jsonplaceholder.typicode.com/users')
    .then((response) => response.json())
    .then((users) => this.setState(
      () =>{
      return {monsters : users}
      },
      ()=> {
        console.log("monsters", this.state.monsters);
      })
    )      


  }

  onSearchChange= (event)=>{ 
    let searchField = event.target.value.toLocaleLowerCase();
    this.setState(()=>{
      return {searchField};

    })
  }

  render(){
    let {monsters, searchField} = this.state;
    let {onSearchChange} = this;
    let filterd = monsters.filter(monster=>{
      return monster.name.toLocaleLowerCase().includes(searchField)
     })
    return (
      <div className="App">
        <h1 className='app-title'>Udemy Learning Course</h1>
        <SearchBox onChangeHandler={onSearchChange} 
        className='monster-search-box'
        placeholder='Search Here'/>
        <CardList monsters={filterd}/>
      </div>
    );
  }


}

export default App;
