import { useEffect } from "react";
import { useState } from "react";

const CategoryList = (props) =>{
    const [setTagList, getTagList] = useState(null);

    

    console.log("Category List", props.categories);

    return (
        <div>

<table className="table" id='report-list' style={{textAlign: 'center'}}>
                <thead>
                    <tr className='col-12'>
                        <th scope="col" className="col-9">Recipient Name</th>
                        <th scope="col" className="col-1">Tags</th>
                    </tr>
                </thead>
                <tbody>
                    {
                    filteredFullWidgetList.map((widget)  => 
                    <tr key={widget.id} >
                        <td scope="row"><input className="form-check-input customCheckboxButton" id="i" value="{i}" checked={widget.isSelected == true}  onChange={(e) => handleOnChange(e, widget.id, widget.isSelected)} type="checkbox" /></td>
                        <td className='black-font-16-sans custom-letterspacing' style={{letterSpacing : "1.5px"}}>{ widget.widgetName }</td>
                        <td className={(widget.isAssigned == 1 ? 'green-font-14-sans' : 'yellow-font-14-sans')} >{`${widget.isAssigned == 1? "Active": "Inactive"}`}</td>
                        <td>1.2 MB</td>
                        </tr> )
                    } 
                </tbody>
            </table>
            
        {props?.categories?.map((item)=> 
           <div   key={item.id}>
            <h2>Receipient Name: {item.name}</h2>
            <span>{(item.tags?.length>0 ? item.tags?.toString() : "No Tags Found")}</span>
          </div>
        )}
        </div>
    )
        
    }

export default CategoryList;
