import logo from './logo.svg';
import './App.css';
import data from './data.json';
import {Component} from 'react';
import SearchBox from './components/Search-Box/searchBox';
import CategoryList from './components/Category-List/categoryList'


class App extends Component{

  constructor(){
    super();
    this.state = {
      categories:[],
      searchField: ''

    }  
  }

  componentDidMount(){
   console.log("data", data);
   this.setState({categories: data.recipients});

  }

   onSearchChange= (event)=>{ 
    let searchField = event.target.value.toLocaleLowerCase();
    this.setState(()=>{
      return {searchField};

    })
  }

  render(){
    let {categories, searchField} = this.state;
    let {onSearchChange} = this;
    let filtered = categories.filter(category=>{
      return category.tags.toLocaleLowerCase().includes(searchField)
     })
    return (
      <div className="App">
        <h1 className='app-title'>Udemy Learning Course</h1>
        <SearchBox onChangeHandler={onSearchChange} />
        {/* <CategoryList categories={filtered}/> */}
        <CategoryList categories={this.state.categories}/>

      </div>
    );
  }


}

export default App;
