import CategoryList from './components/Category-List/categoryList'


class App extends Component{

  constructor(){
    super();
    this.state = {
      categories:[],
      searchField: [{
        input1: "",
        input2: ""
      }]

    }  
  }

  componentDidMount(){
   console.log("data", data);
   this.setState({categories: data.recipients});

  }

