import './App.css';
import data from './data.json';
import {Component} from 'react';


class App extends Component{

  constructor(){
    super();
    this.state = {
      categories:[],
      searchField: ''

    }  
  }

  componentDidMount(){
   console.log("data", data);
   this.setState({categories: data.recipients});

  }

  onSearchChange= (event)=>{ 
    let searchField = event.target.value.toLocaleLowerCase();
    this.setState(()=>{
      return {searchField};

    })
  }

  render(){
    let {categories, searchField} = this.state;
    let {onSearchChange} = this;
    let filterd = categories.filter(category=>{
      return category.name.toLocaleLowerCase().includes(searchField)
     })
    return (
      <div className="App">
        <h1 className='app-title'>Udemy Learning Course</h1>
        {/* <SearchBox onChangeHandler={onSearchChange} 
        className='monster-search-box'
        placeholder='Search Here'/>
        <CardList monsters={filterd}/> */}
      </div>
    );
  }


}

export default App;
