import logo from './logo.svg';
import './App.css';
import data from './data.json';
import {Component} from 'react';
import SearchBox from './components/Search-Box/searchBox';
import CategoryList from './components/Category-List/categoryList'


const initialList = {
  categories: data.recipients
}

class App extends Component{
  constructor(){
    super();
    this.state = {
      categories:[],
      filteredCategories:[],
      searchField: {
        input1: "",
        input2: ""
      }

    }  
  }

  componentDidMount(){
   console.log("data", data);
   this.setState({categories: data.recipients, filteredCategories: data.recipients});

  }

  onSearchChange= (input1, input2)=>{ 
    console.log("event", input1 , input2);
    let filterArray = [];
    let filtered = this.state.categories.filter(category=>
      category?.tags?.includes(input1.trim()) && category?.tags?.includes(input2.trim())
      )
      this.setState({filteredCategories: filtered});
     console.log("FIltered category ", filtered);
  }


  onClearClick= ()=>{ 
    console.log("initialList", initialList);
   this.setState({filteredCategories: initialList.categories})
  }




  render(){
    let {filteredCategories} = this.state;
    return (
      <div className="App">
        <h1 className='app-title'>Cheetah Digital Techinical Test</h1>
        <SearchBox onSubmit={this.onSearchChange} onClear={this.onClearClick} />
        <br></br>
        <CategoryList categories={filteredCategories}/>

      </div>
    );
  }


}

export default App;
